To map a column name, use 

	[Property("blog_name")]
	public int Name
	{
		get { return _name; }
		set { _name = value; }
	}

The following code illustrates the use of a 
nested PostalAddress class

	[ActiveRecord("Companies")]
	public class Company : ActiveRecordBase
	{
		private int id;
		private PostalAddress _address;
	
		public Company()
		{
		}
	
		public Company(string name)
		{
			this.name = name;
		}
	
		[PrimaryKey]
		public int Id
		{
			get { return id; }
			set { id = value; }
		}
	
		[Nested]
		public PostalAddress Address
		{
			get { return _address; }
			set { _address = value; }
		}
	}
	
	public class PostalAddress
	{
		private String _address;
		private String _city;
		private String _state;
		private String _zipcode;
	
		[Property]
		public String Address
		{
			get { return _address; }
			set { _address = value; }
		}
	
		[Property]
		public String City
		{
			get { return _city; }
			set { _city = value;}
		}
	
		[Property]
		public String State
		{
			get { return _state; }
			set { _state = value; }
		}
	
		[Property]
		public String ZipCode
		{
			get { return _zipcode; }
			set { _zipcode = value; }
		}
	}

The following code exemplifies two classes that maps 
to two tables sharing the primary key:

	[ActiveRecord("Employee")]
	public class Employee : ActiveRecordBase
	{
		private int id;
		private Award award;
	
		[PrimaryKey(PrimaryKeyType.Native, "EmployeeID")]
		public int ID
		{
			get { return this.id; }
			set { this.id = value; }
		}
	
		[OneToOne]
		public Award Award
		{
			get { return this.award; }
			set { this.award = value; }
		}
	}
	
	[ActiveRecord("Award")]
	public class Award : ActiveRecordBase
	{
		private Employee employee;
		private int id;
	
		public Award()
		{
		}
	
		public Award(Employee employee)
		{
			this.employee = employee;
		}
	
		[OneToOne]
		public Employee Employee
		{
			get { return this.employee; }
			set { this.employee = value; }
		}
	
		[PrimaryKey(PrimaryKeyType.Foreign, "EmployeeID")]
		public int ID
		{
			get { return this.id; }
			set { this.id = value; }
		}
	
		public static Award[] FindAll()
		{
			return ((Award[]) (ActiveRecordBase.FindAll(typeof(Award))));
		}
	
		public static void DeleteAll()
		{
			ActiveRecordBase.DeleteAll( typeof(Award) );
		}
	}
	Employee emp = new Employee();
	emp.Name = "john doe";
	emp.Save();
	
	Award award = new Award(emp);
	award.Description = "Invisible employee";
	award.Save();

