[ActiveRecord]
public class User : ActiveRecordBase
{
  ...
  
  public static int CountAllUsers()
  {
    return CountAll(typeof(User));
  }
}

[ActiveRecord]
public class User : ActiveRecordBase
{
  ...
  
  public static int CountAllUsersLocked()
  {
    return CountAll(typeof(User), "IsLocked = ?", true);
  }
}

